INSERT INTO `secretes` (
    `SECRETE_TITLE`, `SECRETE_CONTENT`, `SECRETE_AUTHOR`, `SECRETE_THUMBNAIL_URL`,
    `CREATED_AT`, `CREATED_BY`, `MODIFIED_AT`, `MODIFIED_BY`
) VALUES
      ('클라우드 컴퓨팅의 미래', '클라우드 컴퓨팅은 이제 기업의 필수 인프라입니다. 오늘날의 클라우드 기술은 가상화, 자동화, 그리고 확장성 있는 솔루션을 제공하며, 기업의 비용 절감과 효율성을 크게 향상시킬 수 있습니다.', '작성자1', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('인공지능과 머신러닝', '인공지능(AI)과 머신러닝(ML)은 현재 IT 산업에서 가장 핫한 기술입니다. 이러한 기술들은 자동화, 데이터 분석, 예측 분석 등 다양한 분야에서 빠르게 활용되고 있습니다.', '작성자2', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('블록체인의 원리와 활용', '블록체인은 탈중앙화된 방식으로 데이터를 관리하는 기술입니다. 다양한 산업에서 보안성을 높이고, 거래를 안전하게 처리할 수 있는 수단으로 주목받고 있습니다.', '작성자3', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('5G 기술과 미래의 인터넷', '5G는 초고속 인터넷, 대규모 연결, 저지연 서비스를 제공하며 IoT, 자율주행차, 스마트 시티 등 다양한 혁신적인 기술을 가능하게 합니다.', '작성자4', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('사이버 보안의 중요성', '디지털 시대의 도래와 함께 사이버 공격의 위협이 날로 증가하고 있습니다. 이에 따라 기업과 개인의 보안 관리 강화가 절실히 요구됩니다.', '작성자5', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('데브옵스의 이해', '데브옵스(DevOps)는 개발과 운영을 하나로 통합하여 효율적이고 안정적인 서비스 제공을 목표로 하는 문화와 방법론입니다.', '작성자6', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('소프트웨어 아키텍처', '소프트웨어 아키텍처는 시스템의 구조와 상호 작용을 정의하며, 개발 과정에서 품질을 보장하기 위해 필수적인 설계 및 전략을 제공합니다.', '작성자7', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('데이터베이스 최적화 기법', '효율적인 데이터베이스 설계 및 최적화 기법은 웹 애플리케이션의 성능을 향상시키는 중요한 요소입니다. 인덱스 최적화, 쿼리 최적화 등이 필요합니다.', '작성자8', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('기계 학습의 기초', '기계 학습(ML)은 데이터를 기반으로 예측하거나 패턴을 인식하는 알고리즘을 연구하는 분야입니다. 다양한 도메인에서 활용되고 있으며, 특히 데이터 분석 분야에서 중요성을 인정받고 있습니다.', '작성자9', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('IoT 기술의 발전', '사물인터넷(IoT)은 인터넷을 통해 물리적인 기기들을 연결하는 기술로, 스마트홈, 산업 자동화, 헬스케어 등 다양한 분야에 혁신을 일으키고 있습니다.', '작성자10', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('AI와 미래의 일자리', 'AI 기술의 발전은 많은 산업에서 사람의 역할을 대체하고 있습니다. 이에 따라 새로운 일자리의 창출과 기존 일자리의 변화가 필요합니다.', '작성자11', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('자율주행차와 미래의 교통', '자율주행차는 기존의 운전 방식을 혁신하고 있습니다. 이 기술은 교통 사고를 줄이고, 운전자의 편의성을 높이며, 미래의 교통 시스템을 변화시킬 것입니다.', '작성자12', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('AR/VR의 진화', '증강 현실(AR)과 가상 현실(VR)은 게임, 교육, 의료 등 다양한 분야에서 활용되고 있으며, 사용자 경험을 획기적으로 개선할 수 있는 잠재력을 지니고 있습니다.', '작성자13', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('블록체인 보안 기술', '블록체인의 분산 원장 기술은 거래의 투명성을 보장하고, 안전한 데이터 관리를 가능하게 하며, 다양한 산업에서 보안 기술로 채택되고 있습니다.', '작성자14', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('디지털 트랜스포메이션', '디지털 트랜스포메이션은 기술을 통해 기업의 운영 방식을 혁신하는 과정입니다. IT와 비즈니스 전략의 통합이 성공적인 디지털 트랜스포메이션의 핵심입니다.', '작성자15', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('모바일 앱 개발 트렌드', '모바일 앱 개발은 계속해서 진화하고 있으며, 최신 기술을 활용한 앱들은 사용자 경험을 크게 향상시키고 있습니다. 최근에는 AI, AR/VR 기술을 탑재한 앱들이 주목받고 있습니다.', '작성자16', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('오픈소스 소프트웨어', '오픈소스 소프트웨어는 누구나 사용하고 수정할 수 있는 소프트웨어입니다. 많은 기업들이 오픈소스를 활용하여 혁신적인 기술을 개발하고 있습니다.', '작성자17', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('IT와 의료 산업의 융합', 'IT 기술이 의료 산업에 도입됨으로써 많은 혁신이 이루어지고 있습니다. 원격 진료, AI 기반의 진단, 빅데이터 분석 등은 의료 분야의 발전을 이끌고 있습니다.', '작성자18', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('로봇 공학의 혁신', '로봇 공학은 제조업, 물류, 의료 등 다양한 분야에서 활발히 활용되고 있으며, 특히 자동화와 효율성을 크게 개선하는 데 기여하고 있습니다.', '작성자19', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('디지털 보안 기술', '디지털 보안은 오늘날 정보 기술 산업에서 필수적인 요소입니다. 해킹과 사이버 공격을 방지하기 위해서는 강력한 보안 시스템이 요구됩니다.', '작성자20', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('데이터 분석과 예측', '데이터 분석은 기업 의사결정에 중요한 역할을 합니다. 예측 분석을 통해 미래의 트렌드를 파악하고, 시장 변화를 예측할 수 있습니다.', '작성자21', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin'),
      ('스마트 팩토리', '스마트 팩토리는 IoT와 인공지능 기술을 통해 생산성과 품질을 높이는 혁신적인 제조 시스템입니다.', '작성자22', 'https://images.unsplash.com/photo-1520085601670-ee14aa5fa3e8?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', NOW(), 'admin', NOW(), 'admin')
;
